mapper.select:
SELECT MEMBER_NO, MEMBER_NAME, MEMBER_EMAIL, MEMBER_ST, FLOOR(SYSDATE - SECESSION_DT) PASS_ST
FROM MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE SUBS_ORDER SET
S_CANCEL = 'Y'
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT P_ORDER_CD,TOTAL_PRICE,DELIVERY_ST, TO_CHAR(P_ORDER_DT, 'YYYY/MM/DD') P_ORDER_DT,
'[원산지 ' || (SELECT ORIGIN || '] ' || PRODUCT_NM FROM PRODUCT_ORDER JOIN OPTION_TB USING(P_ORDER_CD) JOIN OPTION_TYPE USING(OPTION_CD) JOIN PRODUCT USING(P_CD) JOIN FARM USING(FARM_NO) WHERE P_ORDER_CD = #{orderNo} AND ROWNUM = 1) ORIGIN,
(SELECT COUNT(*) - 1 CNT FROM PRODUCT_ORDER JOIN OPTION_TB USING(P_ORDER_CD) JOIN OPTION_TYPE USING(OPTION_CD) JOIN PRODUCT USING(P_CD) JOIN FARM USING(FARM_NO) WHERE P_ORDER_CD = #{orderNo} ) CNT FROM PRODUCT_ORDER JOIN PRODUCT_DELIVERY USING(DELIVERY_CD) WHERE MEMBER_NO = #{memberNo}
